import React from 'react';
import CardItem from './CardItem';
import gif1 from "../assets/videos/cms-development.gif";
import gif2 from "../assets/videos/CMS-Website-Bgrafio.gif";
import gif3 from "../assets/videos/giphy.gif";
export const Cards = (props) => {
  if(props.page){
  return (

    <div className='cards'>
      <h1>management made easy </h1>
         <div className="cards__container">
            <div className="cards__wrapper">
                <ul className="cards__items">
                    <CardItem 
                    src={gif1}
                   
                    text="Get closer than ever to your users via fully integrated social features including polls, comments, live chat, social media and much more"
                    label='Relationship'
                    path='/services'
                    />
                     <CardItem
              src={gif2}
              text='Manage your website contents, navigation, UI, documents and much more'
              label='Content'
              path='/services'
            />
              <CardItem
              src={gif3}
              text='Make your web site personal for each user; make the userâ€™s journey enjoyable and relevant more than ever'
              label='User Experience '
              path='/sign-up'
            />
                </ul>
            </div>
         </div>
    </div>
  )
  }
}
