import logo from './logo.svg';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"
import 'bootstrap/dist/css/bootstrap.min.css';
import { NavBar } from "./components/NavBar";
import { Banner } from "./components/Banner";
import { Skills } from "./components/Skills";
import { Projects } from "./components/Projects";
import { Contact } from "./components/Contact";
import { Footer } from "./components/Footer";
import { Cards } from "./components/Cards" ;
import { MailchimpForm } from "./components/MailchimpForm" ;
import {Chooseus} from "./components/Chooseus/ChooseUs";
import {Aboutus} from "./components/Aboutus/AboutUs";
import { HeroSection } from "./components/HeroSection" ;
import axios from 'axios';
import { useEffect, useState } from 'react';
import CourseHome from "./components/allcourses/CourseHome"
import Team from "./components/team/Team"



function App() {
  const [error, setError] = useState(null);
  const [homePage, setHomePage] = useState({ 
    Description: "",
    Title: "",
    SubTitle:"",
    Image:"",
    Tab1:"",
    Tab2:"",
    Tab3:"",
    nameinput:"",
    lastnameinput:"",
    messageinput:"",
    Email:"",
    number:"",
    Section1:"",
    section1a:"",
    sectiob1b:"",
    section1c:"",
    section1d:"",
    section1e:"",
    Section2:"",
    section2a:"",
    sectiob2b:"",
    section2c:"",
    section2d:"",
    Section3:"",
    section3a:"",
    sectiob3b:"",
    section3c:"",
    section3d:"",
    Section4:"",
    section4a:"",
    sectiob4b:"",
    section4c:"",
    section4d:"",
    tab1:"",
    tab2:"",
    tab3:"",
    linkAdmin:"",
    admin:"",
    front:"",
    linkFront:"",
    card1button:"",
    card1description:"",
    card2button:"",
    card2description:"",
    card3button:"",
    card3description:"",
    client1:"",
    client2:"",
    client3:"",
    client4:"",
    movingcontent1:"",
    sendbutton:"",});
  useEffect(() => {
    console.log(homePage);
    axios
      .get('http://localhost:1337/api/home-page?populate=deep')
      .then(({ data }) => setHomePage(data.data.attributes))
      .catch((error) => setError(error))
  }, [])




  if (error) {
    // Print errors if any
    return <div>An error occured: {error.message}</div>;
  }
  return (
    <div className="App">

      <NavBar page={homePage.NavBar}/>
      <Banner page={homePage.BannerLayout} />
      <Skills page={homePage.clients}/>
      <Aboutus />
      <Chooseus />
      <Aboutus />
      <Cards page={homePage.cards} />
      <CourseHome />
      <HeroSection/>
      <Team />
      <Projects page={homePage.projects}/>
      <Contact page={homePage.contactbox} />
      <MailchimpForm />
      <Footer page={homePage.Footer}/>




    </div>
  );
}

export default App;
